mapper.update:
UPDATE oa_case SET 
	proc_ins_id = #{processInstanceId}
WHERE id = #{id}

UPDATE oa_case SET 
	assignee_close_case_opt = #{assigneeCloseCaseOption},
	deptLeader_close_flag = #{rejectFlag},				
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_case SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 
	
FROM oa_case a
	
WHERE a.id = #{id}

SELECT
FROM oa_case a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.caseTitle LIKE CONCAT('%', #{caseQueryTitle}, '%') '%'||#{caseQueryTitle}||'%' '%'+#{caseQueryTitle}+'%'
AND a.caseDocNo LIKE CONCAT('%', #{caseQueryDocNo}, '%') '%'||#{caseQueryDocNo}||'%' '%'+#{caseQueryDocNo}+'%'
AND a.parties LIKE CONCAT('%', #{caseQueryParty}, '%') '%'||#{caseQueryParty}||'%' '%'+#{caseQueryParty}+'%'
AND a.legal_agent LIKE CONCAT('%', #{caseQueryLegalAgent}, '%') '%'||#{caseQueryLegalAgent}||'%' '%'+#{caseQueryLegalAgent}+'%'
AND a.address LIKE CONCAT('%', #{caseQueryAddress}, '%') '%'||#{caseQueryAddress}||'%' '%'+#{caseQueryAddress}+'%'
AND a.phone_number LIKE CONCAT('%', #{caseQueryPhoneNumber}, '%') '%'||#{caseQueryPhoneNumber}||'%' '%'+#{caseQueryPhoneNumber}+'%'
AND a.assigneeIds LIKE CONCAT('%', #{caseQueryAssignee.id}, '%') '%'||#{caseQueryAssignee.id}||'%' '%'+#{caseQueryAssignee.id}+'%'
AND a.illegal_construct_flag = #{caseQueryIllegalConstruct}
AND a.case_stage = #{caseQueryStage}
AND a.norm_desc_Part2 = #{caseQueryBrokeLaw}
AND a.norm_penal_Part2 = #{caseQueryPenal}
AND a.caseRegStartDate >= #{caseQueryRegStartDateStart}
AND a.caseRegStartDate >= #{caseQueryRegStartDateEnd}
AND a.caseRegEndDate >= #{caseQueryRegEndDateStart}
AND a.caseRegEndDate >= #{caseQueryRegEndDateStart}
AND a.caseCloseUpEndDate <= #{caseQueryCloseDateStart}
AND a.caseCloseUpEndDate <= #{caseQueryCloseDateEnd}
${sqlMap.dsf} ORDER BY a.create_date DESC
