mapper.select:
SELECT 

FROM oa_doc3 a

WHERE a.id = #{id}

SELECT 

FROM oa_doc3 a

WHERE a.del_flag = #{DEL_FLAG_NORMAL} 

	AND a.create_date >= #{docQueryPublishDateStart}


	AND a.create_date <= #{docQueryPublishDateEnd}


	AND a.doc_title LIKE
			CONCAT('%', #{docQueryTitle}, '%')
			'%'||#{docQueryTitle}||'%'
			'%'+#{udocQueryTitle}+'%'


	AND a.DRStage = #{docQueryStage}

${sqlMap.dsf}
ORDER BY a.id DESC

SELECT 

FROM oa_doc3 a

WHERE a.proc_ins_id = #{processInstanceId}

mapper.update:
UPDATE oa_doc3 SET 
	reviewersIDs1 = #{reviewersIDs1},
	DRStage = #{DRStage},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_doc3 SET 
	DRStage = #{DRStage},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_doc3 SET 
	proc_ins_id = #{processInstanceId}
WHERE id = #{id}

UPDATE oa_doc3 SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE oa_doc3 SET 
	proc_ins_id = #{processInstanceId},
	update_by = #{updateBy.id}, 
	update_date = #{updateDate}
WHERE id = #{id}
