mapper.select:
SELECT a.NAME_ AS 'id', TEXT_ AS 'text' FROM act_ru_variable a
WHERE a.PROC_INST_ID_ = #{processInstanceId}
AND NAME_ LIKE CONCAT('%', #{processInstanceId}, '%')

SELECT a.NAME_ AS 'id', TEXT_ AS 'text' FROM act_hi_varinst a
WHERE a.PROC_INST_ID_ = #{processInstanceId}
AND NAME_ LIKE CONCAT('%', #{processInstanceId}, '%')
