mapper.select:
SELECT 

FROM oa_doc a

WHERE a.id = #{id}

SELECT 

FROM oa_doc a

WHERE a.del_flag = #{DEL_FLAG_NORMAL} 

	AND a.create_date >= #{docQueryPublishDateStart}


	AND a.create_date <= #{docQueryPublishDateEnd}


	AND a.doc_title like (${docTitle})

${sqlMap.dsf}
ORDER BY a.id DESC

mapper.update:
UPDATE oa_doc SET 
	doc_title = #{docTitle}, 
	doc_attach_links = #{docAttachmentLinks},
	approver_ids = #{docApproverIDs},
	due_date = #{dueDate}
WHERE id = #{id}

UPDATE oa_doc SET 
	process_instance_id = #{processInstanceId}
WHERE id = #{id}

UPDATE oa_doc SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
